package ex2_180516;

public class DenyLogicOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean play=true;
		System.out.println(play);
		
		//자바에서!의 의미는 not의미
		play=!play;//play의 반대값대입
		System.out.println(play);
		
		play=!play;//play의 반대값대입
		System.out.println(play);

	}

}
